INSERT INTO calendar (title, description, date, sources) VALUES ('Der legendäre Arbeitspulli-Moment.', 'Erster Auftritt von Drache\'s Arbeitspulli, den er sich demonstrativ im Stream auszieht, um zu beweisen, dass er arbeitet.&lt;br&gt;\nAußerdem streamt er an dem Tag auf Twitch, zusammenfassung aus LS-Forum: \n&lt;ul&gt;\nZusammenfassung:\n&lt;li&gt; Laptops haben keine GraKa&lt;/li&gt;\n&lt;li&gt; Er ist eine Banane&lt;/li&gt;\n&lt;li&gt; Er hat immer noch keine 16k Leitung&lt;/li&gt;\n&lt;li&gt; Er hat dauernd Disconects&lt;/li&gt;\n&lt;li&gt; In seinem Haus rumgehen is für Ihn Sport&lt;/li&gt;\n&lt;li&gt; Bannt Fielmann&lt;/li&gt;\n&lt;li&gt; Hasst das Wort “Heil” und will nicht gesund werden&lt;/li&gt;\n&lt;li&gt; Sein schlechtes Internet is von 1&amp;1&lt;/li&gt;\n&lt;li&gt; 1 Beitrag pro 15sec ist für ihn zuviel&lt;/li&gt;\n&lt;li&gt; Sein “Job” ist super&quot;&lt;/li&gt;\n&lt;li&gt; Seine Maus hat auf seinem Arm mehr Kontaktfläche als auf dem Tisch&lt;/li&gt;\n&lt;li&gt; wurde gebannt ohne Grund &lt;/li&gt;\n&lt;li&gt; Schaut kein fehrnsehn&lt;/li&gt;\n&lt;li&gt; hat was gegen Dschungelcamp&lt;/li&gt;\n&lt;li&gt; kennt keine Stars&lt;/li&gt;\n&lt;li&gt; hat vermutlich Intel Celron Prozessor&lt;/li&gt;\n&lt;li&gt; Als Dragonball abgesetzt wurde hat er aufgehört TV zu schauen&lt;/li&gt;\n&lt;li&gt; Mittwoch ist Bürotag&lt;/li&gt;\n&lt;li&gt; findet Kartoffeln komisch&lt;/li&gt;\n&lt;li&gt; applewar pictures sind die geilstein YT und sogar besser als PewDiePie&lt;/li&gt;\n&lt;li&gt; kennt ron teeger nicht&lt;/li&gt;\n&lt;li&gt; die mäuse sind gestern gestorben&lt;/li&gt;\n&lt;li&gt; lässt die mäuse nicht inruhe&lt;/li&gt;\n&lt;li&gt; Arbeitstechnischer Arbeiter, ein anhaltspunkt!&lt;/li&gt;\n&lt;li&gt; ein geplantes fantreffen, is nicht bekannt wo und wann&lt;/li&gt;\n&lt;li&gt; kein Fantreffen in der Schäune :(&lt;/li&gt;\n&lt;li&gt; Trolle sollen gerade eben angeblich sein Auto klauen oder in der Nacht&lt;/li&gt;\n&lt;li&gt; er isst statt Brot lieber Torte&lt