INSERT INTO calendar (title, description, date, sources) VALUES ('Rock am Härtsfeldsee.', '&lt;p&gt;Rainer fährt mit Ariella zu &quot;Rock am Härtsfeldsee&quot;. Als sich Ariella zu lange mit anderen Personen unterhält, \nfährt Rainer am Abend betrunken nach Hause.&lt;/p&gt;\n\n&lt;blockquote&gt;Als glücklicher Zeltnachbar und Hater, lasse ich euch gerne meine Eindrücke des Abends zukommen.&lt;br&gt;\n&lt;br&gt;\n&lt;br&gt;\n19:30 Uhr kam Ariella am Tagesparkplatz an, welcher aus Parkenden autos und ca 12 weiteren gruppierungen bestand und wartete auf Den Drachenlord und einen Anderen Kaschber weil sie vom richtigen Campingplatz vertrieben wurden und nun dort zelten sollen und sich dort Wiedertreffen wollten. Drachenlord war sichtlich nicht begeistern so ausgegrenzt vom Festival zu sein, und nicht bei der “masse” zelten zu können. Die 3 unterhielten sich ein wenig, bis Drachenlord aufbrach um richtung Konzertgelände zu gehen und sich Dragonforce nzusehen. die anderen 2 wollten lieber auf die nachzügler warten und nicht mit auf das Konzert. Diese trafen gegen 22.00 ein. Danach ging die Gruppe geschlossen weg.&lt;br&gt;\nNach dem Dragonforce Konzert kam Drachenlord alleine zurück.&lt;br&gt;\n&lt;br&gt;\nAriella und ihre Anhänger kamen erst kurz nach dem Children of bodom Konzert zurück. Drache wartete bis zur ankunft der anderen alleine am Camp.&lt;br&gt;\nDrachenlord Ging zu seinem, Wagen und wurde Prompt von 3 vorbeilaufenden erkannt. (2 Mädels ein typ, ziemlich jung ca 17-19 Jahre alt) Diese waren sichtlich erfreut und fassungslos ihr Idol zu treffen. Er verteilte Autogrammkarten und lies Erinnerungsfotos schießen.&lt;br&gt;\n&lt;br&gt;\nEr ging mit den Fans einige schritte weg um sich in ruhe, abseits der mitcamper in Ruhe unterhalten zu können.&lt;br&gt;\nAriella war merkbar angetrunken und fing plötzlich an den Drachenlord als “Drache ist so ein Hurensohn” zu betiteln. dies hielt sich auch sehr sehr lange durch sie redete sich total in rage. ein Großer langbärtiger mitzelter im “camp Drachenlord” versuchte sie zu beruhigen und ihr mitzuteilen dass der Drache nur ca ein meter von ihr entfernt steht und selbstverständlich alles mitanhören musste. Ariella zog ab und verschwand richtung See. Drache fragte den Langbärtigen warum Ariella denn so gemein zu ihm sei. Dieser antwortete dass er jetzt keine Lust habe auf Draches Probleme und Drachenlord saß wie versteinert da.&lt;br&gt;\n&lt;br&gt;\nKurz darauf machte er sich auf die suche nach Ariella um mit ihr in ruhe über diese Sache zu sprechen. Mit einem Fassungslosen gesicht und total weiß im gesicht kam er sehr traurig zum Camp zurück und erzählte den 2 noch anwesenden des Camps dass er Ariella wohl gerade beim sex erwischt habe.&lt;br&gt;\n&lt;br&gt;\nAriella kam irgendwann zurück mit ihrer Begleitung und wurde sofort von dem großen langbärtigen angesprochen warum sie denn das bitte getan hat, der Drache wäre sehr verletzt. Sie meinte nur sie hat kein Bock auf das Gelaber und stig ins auto und parkte einige Meter weiter und verzog sich dort mit dem Kaschber vom see und einem der anderen Camper.&lt;br&gt;\n&lt;br&gt;\nDrache kam zu uns. Es war gegen 4.15 und er wechselte einige Worte mit mir. Danach ging er an sein Auto, packte wütend zusammen, schmiss einen Stuhl durch die Gegend (welcher dann kaputt ging), räumte den von ihm mitgebrachten Campingtisch mit einer wütenden Handbewegung komplett ab, Riss das hinter seinem Wagen stehende Zelt weg und stieg ins Auto und fuhr, sichtlich angetrunken, und weinend, im 1. Gang vom Gelände des RAH.&lt;br&gt;\n&lt;br&gt;\nAm Samstag morgen konnte ich hören, wie Ariella eine sprachnachricht vom handy abspielte, in der Drache fragte : Wie soll es jetzt weitergehen mit uns? Ariella tat es natürlich sehr leid, dass Drache ihrenwegen so früh und sauer abgereist ist.\n&lt;/blockquote&gt;', '1435269600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der dunkle Parabelritter zu Besuch.', '&lt;p&gt;Der dunkle Parabelritter besucht den Drachenlord, um ein Interview aufzunehmen, welches er &lt;a href=&quot;../../2015/7/2&quot;&gt;vier Tage später&lt;/a&gt; veröffentlicht.\nAußerdem nimmt er eine &lt;a href=&quot;../../2015/7/8&quot;&gt;Roomtour&lt;/a&gt; auf.&lt;/p&gt;', '1435442400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Roomtour aus der Schanze.', '&lt;p&gt;Der dunkle #Parabelritter veröffentlicht ein Video, bei dem der Drachenlord ihn durch die #Schanze\nführt. Das Video wurde beim Besuch am 28.06.2015 aufgenommen. Durch das Video wird bekannt, wie runtergekommen und vemüllt die #Schanze ist.&lt;/p&gt;', '1435874400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der Feuerwehreinsatz.', '&lt;p&gt;Der Haider &quot;BasedGod&quot; ruft die Feuerwehr zum Drachenlord in die #Schanze. \nUnter anderem wird er dafür später zu einer &lt;a href=&quot;../../2016/12/14&quot;&gt;Haftstrafe verurteilt&lt;/a&gt;.&lt;/p&gt;', '1436911200', 'www.younow.com/DrachenLord_Offiziell/58217148/7480353/1021/b\nhttps://archive.is/yyif5\nhttps://archive.is/xFed4\nhttps://archive.is/CBC9V\nhttps://feuerwehr-emskirchen.de/einsaetze/details/242');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird bestohlen.', '&lt;p&gt;#Ariella klaut Rainer sein geliebtes Daim-Eis aus der Tiefkühltruhe. Später ärgert er sich in einem Stream, da er nicht weiß, wie sein Eis verschwunden ist.&lt;/p&gt;', '1437084000', 'https://twitter.com/pr8hengst/status/622151480712237056\n\nhttps://www.gutefrage.net/frage/wer-ist-ariella-im-drachengame');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der AsA startet.', '&lt;p&gt;Der &lt;a href=&quot;https://altschauerberganzeiger.com/&quot;&gt;Altschauerberg Anzeiger&lt;/a&gt; wird ins Leben gerufen und \nberichtet ab sofort beinahe täglich über den Drachenlord.&lt;/p&gt;', '1437343200', 'https://altschauerberganzeiger.com/post/124651959916/montag-der-20-juli-2015\nhttps://archive.is/yyif5');
INSERT INTO calendar (title, description, date, sources) VALUES ('DracheOffiziell bei YouNow gestartet.', 'Haider bringen Rainers Facebook-, Skype- und #YouNow Account unter ihre Kontrolle, der \nYouNow-Account &lt;em&gt;DrachenLord_Offiziell&lt;/em&gt; wird gelöscht.\nRainer eröffnet unter &lt;em&gt;Drache_Offiziell&lt;/em&gt; einen neuen YouNow Kanal.', '1437688800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('&quot;Fuck duf Hater&quot; Schild aufgestellt.', '&lt;p&gt;Rainer hat in seinem Hof ein Holzschild mit der Aufschrift &quot;Fuck auf Hater&quot; angebracht. Es sieht aber aus wie &quot;Fuck duf Hater&quot;.\nNach nur 8 Tagen wird das &lt;a href=&quot;../../2015/8/15&quot;&gt;Schild geklaut&lt;/a&gt;.&lt;/p&gt;', '1438812000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der zweite Feuerwehreinsatz.', '&lt;p&gt;Nach dem 15.7.2015 wird erneut die Feuerwehr in die #Schanze gerufen.&lt;/p&gt;', '1439071200', 'https://feuerwehr-emskirchen.de/einsaetze/details/246');
INSERT INTO calendar (title, description, date, sources) VALUES ('Summer Breeze.', 'Rainer geht auf das Summerbreeze Festival in Dinkelsbühl und wird dort von Fans erkannt.', '1439416800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('&quot;Fuck duf Hater&quot; Schild wird entwendet.', '&lt;p&gt;Während der Drache auf dem #SummerBreeze sein Fantreffen hält, wird das &lt;a href=&quot;../../2015/8/6&quot;&gt;Fuck duf Hater&lt;/a&gt; Schild von \nder #Schanze entwendet und die Haustüre mit Bauschaum verklebt.&lt;/p&gt;\n&lt;p&gt;Rainer kehrt abends wieder vom Summer Breeze zurück.&lt;/p&gt;', '1439589600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der legendäre Mettwoch.', '&lt;p&gt;Drachenlord macht auf #Younow im Livestream einer #Mulle mit dem Namen #Erdbeerchen, die er zuvor noch nie gesehen\nhat, einen Heiratsantrag und wird darauf von &lt;a href=&quot;../../../tag/dorian&quot; class=&quot;tag&quot;&gt;&quot;Dorian dem Übermenschen&quot;&lt;/a&gt; verarscht.&lt;/p&gt;', '1439935200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('KuchenTalks über den Drachenlord.', '&lt;p&gt;KuchenTV macht ein Video über den Drachenlord.&lt;/p&gt;', '1440626400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('YouNow-Stream mit Braui93.', '&lt;p&gt;Rainer präsentiert seine Taschenmuschi und zitiert den Spruch &quot;Es gibt nicht zu jung. sondern nur zu zu eng&quot;&lt;/p&gt;', '1442008800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Die Hausdurchsuchung.', '&lt;p&gt;Im &lt;a href=&quot;../../date/2015/9/15&quot;&gt;Herbst 2015&lt;/a&gt; bekam Rainer diverse \nGegenstände geliefert, von denen er dachte, dass es Geschenke seiner Fans seien. \nDazu zählte unter anderem ein Desktop-PC. Daraufhin stellte sich prompt heraus, \ndass die Artikel mit geklauten Kreditkarten erworben wurden und dass sich \ndahinter eine Aktion von &quot;BasedGod&quot; verbarg, welcher schon den \n&lt;a href=&quot;../../date/2015/7/15&quot;&gt;Feuerwehreinsatz&lt;/a&gt; ausgelöst hatte. Ein weiteres \nMal erschien die #Polizei und konfiszierte die Geräte. Zunächst blieb Rainer ohne \nPC, womit er streamen könnte, twitterte aber über seine Wii, welche die Polizei \nübersehen hatte. Daraufhin erhielt er von #Berndian einen Leih-Laptop, mit dem er \nweiterstreamen konnte. Dieser enthielt aber angeblich Spyware, um Rainers \nAktivitäten auszuspionieren, worauf er der Polizei übergeben wurde.&lt;/p&gt;', '1442527200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('RBS wird Partner auf Twitch.', '&lt;p&gt;Einer der aktivsten Haider &quot;Regenbogenschaf&quot; wird Partner bei Twitch. Dies regt Rainer auf, da er selber nicht Twitch Partner ist.&lt;/p&gt;', '1443218400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird in die Sendung von Berndian eingeladen.', '&lt;p&gt;Rainer wird in die Radioshow von #Berndian eingeladen. #RBS und #Ariella sind auch vertreten.&lt;/p&gt;', '1443304800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Facebook Profil wird gesperrt.', '&lt;p&gt;Das Facebook Profil vom Drachenlord wird mal wieder gesperrt.&lt;/p&gt;', '1445551200', 'https://altschauerberganzeiger.com/post/131953591831/das-wochenende-des-drachen');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer erstellt einen Snapchat-Account.', '&lt;p&gt;Rainer erstellt (zum ersten Mal) einen Snapchat-Account, den er &quot;dracheoffiziell&quot; nennt.&lt;/p&gt;', '1445637600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('DrachenVlog gegründet.', '&lt;p&gt;Rainer legt den YouTube-Account &quot;DrachenVlog&quot; an&lt;/p&gt;', '1445724000', 'https://socialblade.com/youtube/channel/UCEVT7dAjttw0zOuTVb2lYKg');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird Partner auf YouNow.', 'Der Drachenlord wird Partner bei #Younow. So kann er ab sofort Barren schnorren\nund mit seinen Streams Geld verdienen.', '1445986800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Hass ist keine Emotion, A Frosch und der Lena-Gate.', '&lt;p&gt;Rainer streamt während der Nacht über 9 Stunden lang.&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;&quot;A Frosch!&quot;&lt;/strong&gt;&lt;br&gt;\nWährend des Frühstücks sieht sich Rainer den Pixar-Film &quot;Oben&quot; an. Bei einer \nSzene mit einem Frosch entfährt es ihm: &quot;Ha ha, hahaha, a Frosch!&quot; (ab 08:31:30)&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;Rollstuhl-Lena-Gate&lt;/strong&gt;&lt;br&gt;\nAufgrund der falschen Annahme, dass er wieder mal getrollt werde, beleidigt \nRainer die im Rollstuhl sitzende Zuschauerin Lena aufs übelste (ab 08:43:20).&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;&quot;Hass ist keine Emotion!&quot;&lt;/strong&gt;&lt;br&gt;\nGegen Ende des Streams erklärt uns Rainer, dass Hass keine Emotion sei \n(ab 09:06:50).&lt;/p&gt;', '1447196400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird wegen Meineids verurteilt.', '&lt;p&gt;Der Lügenlord wird wegen Meineids verurteilt, da er vergessen hat, sein Erbe bei einer Vermögensauskunft anzugeben.&lt;/p&gt;', '1447369200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Bombenanschlag auf die Schanze.', '&lt;p&gt;Der Schaflamische Staat wird ausgerufen.&lt;/p&gt;', '1449615600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Die Schanze wird mit Eiern beworfen.', '<p>Die Schanze wird während eines HitBox-Streams zum ersten Mal (?) mit Eiern beworfen.</p>', '1450393200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer löscht seinen Twitch-Account.', '&lt;p&gt;Rainer löscht seinen Twitch Account.&lt;/p&gt;', '1450652400', 'http://droche.rf.gd/?seite=3459');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer täuscht seinen Tod vor.', '&lt;p&gt;Rainer lädt ein Video hoch, in dem bekannt wird, dass der Drachenlord tot ist. Natürlich stellt sich das als Fake raus.&lt;/p&gt;', '1451343600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der ASB-Express startet.', '&lt;p&gt;Mit dem &lt;a href=&quot;http://www.asbexpress.com/&quot;&gt;Asbexpress&lt;/a&gt; gibt es nun neben dem &lt;a href=&quot;../../2015/7/20&quot;&gt;Altschauerberg Anzeiger&lt;/a&gt; eine \nzweite Online-Zeitung, die täglich über den Drachenlord berichtet.&lt;/p&gt;', '1451602800', 'http://www.asbexpress.com/2016/01/01/classic-drachenlord-lets-play-mario-sunshine-best-of/');
INSERT INTO calendar (title, description, date, sources) VALUES ('TS 2 startet', '&lt;p&gt;Rainer eröffnet erneut einen TeamSpeak Server. Außerdem wird an diesem Tag das Grab von Rudi W. besucht.&lt;/p&gt;', '1451948400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('TS2 endet', '&lt;p&gt;Rainer schließt nach kürzester Zeit seinen zweiten Teamspeakserver wieder.&lt;/p&gt;', '1452121200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('KuchenTV besucht Emskirchen.', '&lt;p&gt;Infolge dieses Besuchs wurden viele Kuchenkinder ins Game gespült.&lt;/p&gt;', '1454713200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer gründet eine Steam-Gruppe.', '&lt;p&gt;Drachenlord gründet eine Gruppe in der Steam Community.&lt;/p&gt;', '1457478000', 'https://steamcommunity.com/groups/DracheOffiziell');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der Hamster ist gestorben.', 'Rainer hatte behauptet, dass sein Hamster einen Winterschlaf hält, kommt aber dann doch zu der Erkenntnis, dass der Hamster tot sein muss.', '1458946800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer plant umzuziehen.', '&lt;p&gt;Rainer packt einige Kisten in seinem Video &quot;Der Umzug beginnt&quot;, da er in einigen Monaten plant umzuziehen. Die legendäre DVD-Sammlung wird auch gezeigt.&lt;/p&gt;', '1460498400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Erstes Wiggsfidio per Whatsapp verschickt', 'Rainer verschickt per Whatsapp ein Video in dem zu sehen ist, wie er sich einen \nrunterholt.', '1463090400', 'https://twitter.com/CptMeddl/status/1160995961495261184');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer schmeißt seine Axt.', 'Fans haben beschlossen, dem Drachen einen Rollator zu schenken. Dieser hat sich so sehr darüber &quot;gefreut&quot;, dass er ihnen direkt mal die Axt nausgschmaßt hat.', '1464300000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Aufgelauert und zugeschlagen.', 'Rainer schlägt die Seitenscheibe eines vorbeifahrenden Autos mit einem Stock ein und verletzt eine Person leicht. \nSpäter muss er sich vor &lt;a href=&quot;30&quot;&gt;Gericht&lt;/a&gt; dafür verantworten.\n&lt;p&gt;Im Video, wo er am nächsten Tag zu diesem Vorfall Stellung nimmt, vergleicht er den Hate gegen ihn mit der Verfolgung der Juden durch die Nationalsozialisten.', '1464991200', 'https://www.youtube.com/watch?v=vHfDYehsQ3U');
INSERT INTO calendar (title, description, date, sources) VALUES ('YouNow-Stream mit Boneclinks.', '&lt;p&gt;Boneclinks ist zu Gast in Rainers YouNow-Stream.&lt;/p&gt;', '1467410400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Ich bin das höchste Wesen, das es gibt.', '&lt;p&gt;Zitat Rainer im YouNow-Stream: “Gott kommt zu mir und will von mir gesegnet werden […] Ich bin das höchste Wesen, das es gibt.” (ab 32:23)&lt;/p&gt;', '1467928800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Drachenlord als Marke.', '&lt;p&gt;Rainer versucht &quot;Drachenlord&quot; als Marke einzutragen und scheitert dabei. Am &lt;a href=&quot;https://drachenchronik.gq/date/2019/10/16&quot;&gt;16.10.2019&lt;/a&gt; wird die Marke dann\ndoch angemeldet, allerdings nicht von Rainer.&lt;/p&gt;\n&lt;p&gt;Zudem wird er von seiner Freundin angerufen, während er ein Video dreht (BabeNichtJetzt, ab 4:40)&lt;/p&gt;', '1468706400', 'https://register.dpma.de/DPMAregister/marke/register/3020161063505/DE');
INSERT INTO calendar (title, description, date, sources) VALUES ('Kein Strom / Product Placement.', '&lt;p&gt;Rainer erzählt in einem &lt;a href=&quot;https://drachenchronik.gq/video/watch/oXy9LvRFj9w&quot;&gt;Video&lt;/a&gt;, dass ihm der \n#Strom abgestellt wurde. Es wird fast 4 Monate dauern, bis es &lt;a href=&quot;../../2016/11/11&quot;&gt;wieder Strom&lt;/a&gt; an der #Schanze gibt.&lt;/p&gt;', '1469052000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer in den russischen Nachrichten.', '&lt;p&gt;Das russische Fernsehen berichtet in einer Nachichtensendung über den \n&lt;a href=&quot;https://de.wikipedia.org/wiki/Anschlag_in_M%C3%BCnchen_2016&quot;&gt;Amoklauf in München&lt;/a&gt; und sucht bei Twitter nach dem Täter. \nIrgendein Troll mit dem Namen @FaulOffiziell postet dort ein Bild vom Drachenlord, das vom russischen Fernsehen ohne es zu prüfen übernommen wird.&lt;/p&gt;', '1469484000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird mit Eiern beworfen.', '&lt;p&gt;Der Drachenlord bekommt Besuch von einem maskierten Haider, der ihn mit Eiern bewirft.&lt;/p&gt;', '1469829600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('BabyChrizzy trifft sich mit dem Drachenlord.', 'Die YouTuberin &quot;BabyChrizzy&quot; besucht den Drachenlord.', '1469916000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der erste Posterverkauf.', '&lt;p&gt;Rainer startet seinen ersten Posterverkauf auf seinem Hof. Wer eines käuflich\nerwarb, durfte sich sogar auf seiner &quot;Fanwand&quot; verewigen.&lt;/p&gt;', '1470434400', 'https://altschauerberganzeiger.com/post/148578675111/060816-posterverkauf-am-schauerberg');
INSERT INTO calendar (title, description, date, sources) VALUES ('Haider greift Rainer mit Schlagstock an.', '&lt;p&gt;Rainer wird mit einem Schlagstock angegriffen. An diesem Tag wird er aber\nauch von #iBlali für die Roast Yourself Challenge nominiert.&lt;/p&gt;', '1471039200', 'https://twitter.com/sash_api/status/764554832170582017\n\nhttps://www.flickr.com/photos/modernezeiten/28376101924/');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer im Tiergarten, mit der Zoomulle.', '&lt;p&gt;Rainer besucht in weiblicher Begleitung den Tiergarten Nürnberg. Ein\ndazugehöriges Video veröffenlicht er am 11.10.2016, als er sich anlässlich\ndes Drehs für World of Wolfram in Köln befindet und ihm im Hotelzimmer der zum\nHochladen des Videos benötgte Strom zur Verfügung steht.&lt;/p&gt;', '1474408800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird von ProSieben erwähnt.', '<p>Der Rainer wird in einem Tweet von ProSieben erwähnt.</p>', '1474495200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der Disco-Rausschmiss.', '&lt;p&gt;Rainer besucht in Begleitung der #Zoomulle ein Metal-Konzert im \n&lt;a href=&quot;https://www.der-hirsch.com/&quot;&gt;&quot;Hirsch&quot;&lt;/a&gt; in Nürnberg. Im Laufe des\nAbends wird er aus dem Lokal geworfen, weil er sich mit einem anderen Gast \nlautstark gezofft hat.&lt;/p&gt;', '1474581600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der erste dokumentierte Einbruch.', '&lt;p&gt;Ein Haider bricht in die Schanze ein und wird dabei von Rainer erwischt, der die Polizei ruft. Der Haider wird zu einer geringen Geldstrafe verknackt.&lt;/p&gt;', '1474668000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Die DrachenBox erscheint.', 'Rainer kündigt im Zuge des Posterverkaufs die &quot;DrachenBox&quot; an. Diese konnte man beim Posterverkauf für 5 Euro erwerben. Der Inhalt war zum Großteil Kleinkram Müll der in der Schanze rumlag.&lt;br&gt;', '1475186400', 'Altschauerberg Anzeiger');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer besucht Köln.', '&lt;p&gt;Der Drachenlord fährt für 2 Tage nach Köln für die &quot;World of Wolfram&quot;-Dreharbeiten. Er wird aber in der fertigen Folge wieder rausgerendert, weil die Produzenten den Kotmidas-Effekt fürchten. \nGleichzeitig findet das erste offizielle Fantreffen in Köln statt. Drache leaked sein Hotel und muss umziehen.&lt;/p&gt;', '1476050400', 'https://altschauerberganzeiger.com/post/151605432771/liveticker-drache-besucht-k%C3%B6ln\nhttps://www.youtube.com/watch?v=hMYdZuOwhP0\nhttps://www.youtube.com/watch?v=JuaLJaSaXGo');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der zweite Tag in Köln.', '&lt;p&gt;Der Drachenlord befindet sich in &lt;a href=&quot;../../2016/10/10&quot;&gt;Köln&lt;/a&gt;.&lt;/p&gt;', '1476136800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer ist zurück aus Köln.', '&lt;p&gt;Der Drachenlord ist wieder zurück aus &lt;a href=&quot;../../2016/10/10&quot;&gt;Köln&lt;/a&gt;.&lt;/p&gt;', '1476223200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer vor Gericht.', '&lt;p&gt;Wegen dem Ereignis am 04.06.2016 muss Rainer vor #Gericht. \nDort wird er wegen Körperverletzung und Sachbeschädigung zu einer Geldstrafe von 60 Tagessätzen a 15€ = 900€ verurteilt.&lt;/p&gt;', '1477260000', 'http://www.asbexpress.com/2016/10/25/lachschongrind-gerichtsverhandlung-vom-drachenlord-aufgelauertundzugeschlagen/');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer hat wieder Strom.', 'Der Drachenlord hat nach &lt;a href=&quot;../7/21&quot;&gt;über 3 Monaten&lt;/a&gt; dank einem Prepaid Stromzähler wieder #Strom und kann ab nun wieder \nVideos produzieren. Außerdem streamt er ab jetzt nicht mehr oben aus seinem alten Kinderzimmer, sondern unten vorm Greenscreen.', '1478818800', 'https://www.reddit.com/r/Drachenlord/comments/5cgt9m/der_lord_ist_live_und_hat_anscheinend_wieder_strom/');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer bezeichnet seine Mutter als &quot;dumme Schlampe&quot;.', '&lt;p&gt;Rainer bezeichnet seine Mutter im Stream als &quot;dumme Schlampe&quot; (49:09 Abendstream).&lt;/p&gt;', '1479250800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Die erste Dokumentation.', '&lt;p&gt;Dennis #Leiffels veröffentlicht auf dem Youtube Kanal des Y-Kollektiv eine 20 minütige \nDokumentation über den Drachenlord.&lt;/p&gt;', '1479942000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Werbeplakat von Haidern wird aufgehängt.', '&lt;p&gt;Mehrere Haider haben zusammengelegt, um ein Werbeplakat mit der Aufschrift &quot;Besiege dein inneres Schaf und geh arbeiten&quot; in Emskirchen\naufhängen zu lassen.&lt;/p&gt;', '1481583600', 'http://www.asbexpress.com/2016/12/13/zusendungsgrind-werbeplakat-vom-drachenlord-endlich-in-emskirchen-angebracht/');
INSERT INTO calendar (title, description, date, sources) VALUES ('Gerichtsverhandlung.', 'BasedGod wurde wegen Kreditkartenmissbrauch, Falschbestellungen \nund dem &lt;a href=&quot;../../2015/7/15&quot;&gt;Notruf beim Drachenlord&lt;/a&gt; zu 3 Jahren und 5 Monaten Freiheitsstrafe verurteilt.', '1481670000', 'https://motherboard.vice.com/de/article/9aqy9y/erstes-deutsches-swatting-hohe-freiheitsstrafe-fuer-drachenlord-hater\nhttps://www.giga.de/webapps/youtube/news/nach-swatting-drachenlord-peiniger-muss-ins-gefaengnis/\nhttps://www.youtube.com/watch?v=p8iXXXdBOiU');
INSERT INTO calendar (title, description, date, sources) VALUES ('Spendenstream für Stromschulden, und neuen PC.', '&lt;p&gt;Rainer startet auf #Hitbox den lange angekündigten Spendenstream, um Geld für\nseine 15\'000 € #Strom Schulden und \neinen neuen #PC zu sammeln. An diesem Tage kamen 64 € zusammen. Die\nSpendenstreams wurden an den beiden folgenden Tagen auf #Hitbox fortgesetzt.\nSchlussendlich nahm er tatsächlich 398.90 € ein.&lt;/p&gt;', '1482879600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('TS3 startet.', '&lt;p&gt;Rainer startet wieder mal einen neuen Teamspeakserver.&lt;/p&gt;', '1484002800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird bei HitBox gebannt.', '&lt;p&gt;Aufgrund &quot;eines Verstoßes gegen die Nutzungsbedingungen&quot; wird Rainers Hitbox-Account gesperrt.&lt;/p&gt;', '1484262000', 'https://altschauerberganzeiger.com/post/155846046686/130117-rip-box\nhttps://www.asbexpress.com/2017/01/14/zusendungsgrind-hitbox-kanal-vom-drachenlord-geloscht/');
INSERT INTO calendar (title, description, date, sources) VALUES ('Y-Kollektiv Doku gelöscht.', '&lt;p&gt;Die Y-Kollektiv Doku über den Drachenlord wird bei Youtube gelöscht.&lt;/p&gt;', '1485471600', 'https://twitter.com/Y_Kollektiv/status/825019785117175808');
