INSERT INTO calendar (title, description, date, sources) VALUES ('Der Gumbel.', '<p>Rainer bekommt zu seinem Geburtstag Besuch von mehreren Haidern und wird gegen Mittag von einem Freund abgeholt. Unter anderem kriegt er auch Besuch von Affenkeks. Mehrere Haider (inkl. Affenkeks) fahren an der Schanze vorbei, und Rainer wirft grundlos in diesem Moment einen Stein gegen dieses Auto. Es kommt zu einem Gerangel, wobei der Kumpel von Affenkeks Rainers T-Shirt zerreißt und seinen Thorhammer klaut.</p>', '1533160800', 'https://www.youtube.com/watch?v=NFIOxlVisPg\nhttps://www.youtube.com/watch?v=xPTWALqXJHw');
INSERT INTO calendar (title, description, date, sources) VALUES ('Erstes Versammlungsverbot.', '&lt;p&gt;Um das das &lt;a href=&quot;../../2018/8/20&quot;&gt;Schanzenfest&lt;/a&gt; zu verhindern, wird für das Gemeindegebiet Emskirchen ein \n&lt;a href=&quot;https://www.kreis-nea.de/uploads/tx_news/allgemeinverfuegung_versammlungsverbot_20180817.pdf&quot;&gt;Versammlungsverbot&lt;/a&gt; erlassen.&lt;/p&gt;', '1534456800', 'https://www.kreis-nea.de/uploads/tx_news/allgemeinverfuegung_versammlungsverbot_20180817.pdf');
INSERT INTO calendar (title, description, date, sources) VALUES ('Das Schanzenfest.', '&lt;p&gt;Im Internet verabreden sich tausende Haider, um den Drachenlord zu besuchen. Die ersten Aufrufe dazu gab es bereits im\nApril 2018. Die Sache hat sich dann immer mehr verselbstständigt. Tatsächlich kommen trotz Versammlungsverbot mehrere hundert Besucher.\nDer Drachenlord sitzt in der #Schanze und streamt auf #Younow, während die #Polizei sein Haus bewacht. Für sein leibliches Wohl grillt \ner in seinem Garten. Alle größeren Medien berichten darüber.&lt;/p&gt;\n&lt;p&gt;Der Drachenlord stellt daraufhin alle Videos auf seinem Kanal &lt;a href=&quot;../../2018/8/22&quot;&gt;auf privat&lt;/a&gt;, veröffentlicht sie aber wenige Tage später wieder.&lt;/p&gt;', '1534716000', 'https://www.focus.de/regional/bayern/youtuber-drachenlord-forderte-user-heraus-videoaufruf-loest-massen-demonstration-aus-polizisten-von-bvb-pokalspiel-abgezogen_id_9450005.html\nhttps://www.br.de/nachrichten/netzwelt/drachenlord-wenn-online-mobbing-real-wird,R1GlF4e');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer legt seinen Hauptkanal still.', '&lt;p&gt;Kurz nach dem &lt;a href=&quot;../../2018/8/20&quot;&gt;Schanzenfest&lt;/a&gt; stellt der Drachenlord alle seine Videos auf dem Hauptkanal auf privat.&lt;/p&gt;', '1534888800', 'https://www.br.de/nachrichten/bayern/youtube-kanal-des-drachenlord-stillgelegt,R1RpOWI\nhttps://www.noz.de/deutschland-welt/digitale-welt/artikel/1502259/nach-hass-demo-youtube-kanal-von-drachenlord-stillgelegt');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer im österreichischen Fernsehen.', 'Der Drachenlord gibt dem österreichischen Sender &quot;ORF&quot; ein kurzes Interview.', '1535320800', 'https://tvthek.orf.at/profile/ZIB-Magazin/5521881/ZIB-Magazin/13987126');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer ist Headline News auf Zeit-Online / Schanzis erster Auftritt.', '&lt;p&gt;Der Drachenlord bekommt einen sehr \n&lt;a href=&quot;https://www.zeit.de/digital/internet/2018-08/youtuber-drachenlord-altschauenberg-cybermobbing-drachengame&quot;&gt;ausführlichen Artikel&lt;/a&gt;\nauf Zeit online spendiert, welcher einen halben Tag lang an erster Stelle als Hauptnews steht.&lt;br&gt;&lt;br&gt;\n\n&lt;h2&gt;Schanzi das Bannschwein&lt;/h2&gt;\n#Schanzi das gehäkelte Minischweinchen hat den ersten Auftritt in Rainers YouNow-Stream.&lt;br&gt;', '1536098400', 'https://www.zeit.de/digital/internet/2018-08/youtuber-drachenlord-altschauenberg-cybermobbing-drachengame');
INSERT INTO calendar (title, description, date, sources) VALUES ('&quot;Drache\'s Block&quot; geht online.', '&lt;p&gt;Drache eröffnet bei #Blogger seinen neuen &quot;&lt;a href=&quot;https://draches-block.blogspot.com/&quot;&gt;Block&lt;/a&gt;&quot; \nmit einem Eintrag über den 20.8.2018 (Schanzenfest).\nDa verhältnismäßig wenige Schreibfehler in dem Beitrag vorhanden sind, wird gemunkelt, dass er entweder Hilfe hatte oder Autocorrect gut geraten hat.&lt;/p&gt;\n\nGold Quote: \n&lt;blockquote&gt;Ich bin sowohl Inland als auch international bekannt rein Logisch betrachtet müsste man sagen das ich wohl mehrere Millionen \nAbonnenten habe. Ich will mich nicht beschweren finde es nur vom Logik Faktor Komisch das genau das nicht der Fall ist.&lt;/blockquote&gt;', '1536444000', 'https://draches-block.blogspot.com/');
INSERT INTO calendar (title, description, date, sources) VALUES ('Zulassung der Rundfunklizenz.', '&lt;p&gt;Die “Kommission zur Ermittlung der Konzentration im Medienbereich”, (KEK) hat in ihrer 239. Sitzung die Zulassung des \nInternet-Streaming-Angebots “Drachenlord” durchgewunken. \nBereits am 29.06.2018 wurde die &lt;a href=&quot;https://www.kek-online.de/medienkonzentration/verfahren/kek-978/&quot;&gt;Lizenz&lt;/a&gt; beantragt.&lt;/p&gt;\n&lt;p&gt;Am 18.12.2018 wird sie ihm wieder entzogen.&lt;/p&gt;', '1536616800', 'https://www.kek-online.de/medienkonzentration/verfahren/kek-978/\nhttps://archive.is/w2AHU\nhttp://beta.gamesmarkt.de/details/433385\nhttps://altschauerberganzeiger.com/post/178068289921/130918-kawaiinerles-shinkansen-spezial');
INSERT INTO calendar (title, description, date, sources) VALUES ('Computer kaputt.', '&lt;p&gt;Rainers Computer geht kaputt, weil er den USB-Killer, der vor seiner Schanze lag, unüberlegt in seinen Computer gesteckt hat. \nBis er einen neuen bekommt, muss er mit seinem Handy Barrne erbetteln.&lt;/p&gt;', '1536703200', 'https://altschauerberganzeiger.com/post/178372247956/220918-bc-gombjuda-mafia');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer (erneut) im Fernsehen.', '&lt;p&gt;Ein weiterer Fernsehbeitrag über die Lordschaft. Diesmal durfte das Fernsehteam für die Interviews sogar in die Schanze vordringen.\nRainer inszeniert sich als armes Mobbingopfer. Der Beitrag ist eher schlecht recherchiert.&lt;/p&gt;', '1537480800', 'https://www.3sat.de/mediathek/?mode=play&amp;obj=75973\nhttps://www.youtube.com/watch?v=kN8Hkdec_CA');
INSERT INTO calendar (title, description, date, sources) VALUES ('Kleine Allgemeinverfügung für Altschauerberg.', '&lt;p&gt;Per Allgemeinverfügung wird versucht, Haider davon abzuhalten, den Drachenlord zu besuchen.&lt;/p&gt;\n&lt;blockquote&gt;\n&lt;p&gt;&lt;b&gt;Der Markt Emskirchen erlässt folgende Allgemeinverfügung:&lt;/b&gt;&lt;/p&gt;\n&lt;p&gt;1. Für den Bereich der Gemarkungen Emskirchen und Schauerberg, bzw. Teilbereiche hiervon gemäß nachfolgender Beschreibung, werden folgende Anordnungen getroffen:&lt;/p&gt;\n&lt;p&gt;1.1 Es wird verboten, eine andere Person oder die Allgemeinheit mit einer Handlung durch a) Lärm b) für Dritte wahrnehmbare obszöne, beleidigende und provozierende Äußerungen unzumutbar zu belästigen, zu behindern, zu gefährden oder zu schädigen. Gültigkeitsbereich: siehe Lageplan - gestreifte Markierung.&lt;/p&gt;\n&lt;p&gt;1.2 Es wird verboten, dass sich eine größere Anzahl von Menschen (Menschenansammlung - mehr als 12 Personen) auf bewohnten oder unbewohnten Grundstücken sowie auf Straßen, Wegen und Plätzen innerhalb von Altschauerberg aufhält. Gültigkeitsbereich: siehe Lageplan - Markierung.&lt;/p&gt;\n&lt;p&gt;1.3 Es wird verboten, den Bereich der Burgruine auf dem Grundstück Fl.Nr. 1128 Gemarkung Emskirchen, zu betreten. Dies gilt nicht für die Eigentümer und Bewirtschafter dieser Grundstücke und Personen, welchen die Grundstückseigentümer und Bewirtschafter das Betreten erlaubt haben sowie für Personen mit einem Amts- bzw. Dienstauftrag. Gültigkeitsbereich: Vor Ort mit Absperrband und Hinweisschilder markiert.&lt;/p&gt;\n\n&lt;p&gt;2. Der Markt Emskirchen kann auf Antrag Ausnahmen von den Anordnungen in Ziffer 1 zulassen.&lt;/p&gt;\n&lt;p&gt;3. Die Allgemeinverfügung gilt bis 01.01.2019.&lt;/p&gt;\n&lt;p&gt;4. Die sofortige Vollziehung der Ziffer 1 wird angeordnet.&lt;/p&gt;\n&lt;p&gt;5. Diese Allgemeinverfügung tritt am Tage nach der Bekanntmachung in Kraft.&lt;/p&gt;\n\nHinweise:\n&lt;ul&gt;\n    &lt;li&gt;Bei Zuwiederhandlungen gegen die Ziffer 1 behält sich der Markt Emskirchen vor, kostenpflichtig Verwaltungszwangsmaßnahmen anzudrohen (Art. 29 ff. Bayerisches Verwaltungszustellungs- und Vollstreckungsgesetz).&lt;/li&gt;\n    &lt;li&gt;Zuwiderhandlung können zudem jedenfalls teilweise als Ordnungswidrigkeit geahndet werden.&lt;/li&gt;\n    &lt;li&gt;Die Allgemeinverfügung wird gemäß Art. 41 Abs. 4 und Art. 27a des Bayerischen Verwaltungsverfahrensgesetzes (BayVwVfG) im verfügenden Teil durch Aushang am Rathaus, im Internet unter www.emskirchen.de und Veröffentlichung im Marktboten ortsüblich bekannt gemacht und kann mit der Begründung beim Markt Emskirchen, Ordnungsamt, Zimmer 7, Erlanger Straße 2, 91448 Emskirchen von jedermann während der allgemeinen Öffnungszeiten eingesehen werden. Die Veröffentlichung erfolgt durch Aushang am Rathaus am Montag, 24.09.2018, die Allgemeinverfügung gilt somit am 24.09.2018 als bekanntgegeben und tritt am Dienstag, 25.09.2018 in Kraft.&lt;/li&gt;\n&lt;/ul&gt;\n[...]\n\n&lt;/blockquote&gt;', '1537740000', 'https://twitter.com/grknzrstrr/status/1046336436222480384\nhttps://altschauerberganzeiger.com/post/178629244511/300918-drache-die-geschichte-hinter-dem\nhttps://twitter.com/Schmutzlard/status/1046040152026632192');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer besitzt einen neuen Computer.', '&lt;p&gt;Nachdem am 12.9.2018 sein PC kaputt gegangen ist, hat Rainer nun endlich alle Teile für einen neuen PC komplett.&lt;/p&gt;\n&lt;ul&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B06XNRQHG4&quot;&gt;CPU&lt;/a&gt;&lt;/li&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B06Y47ZND5&quot;&gt;Mainboard&lt;/a&gt;&lt;/li&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B00UFBZOJQ&quot;&gt;RAM&lt;/a&gt;&lt;/li&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B01GRCYPE6&quot;&gt;Grafikkarte&lt;/a&gt;&lt;/li&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B009VV56TY&quot;&gt;CPU Kühler&lt;/a&gt;&lt;/li&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B078X22YBS&quot;&gt;Netzteil&lt;/a&gt;&lt;/li&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B01M211K53&quot;&gt;250GB SSD&lt;/a&gt;&lt;/li&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B0088PUEPK&quot;&gt;1TB HDD&lt;/a&gt;&lt;/li&gt;\n    &lt;li&gt;&lt;a href=&quot;https://www.amazon.de/gp/product/B00UNJRDEE&quot;&gt;Gehäuse&lt;/a&gt;&lt;/li&gt;\n&lt;/ul&gt;', '1538172000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Neues Versammlungsverbot.', '&lt;p&gt;Für das Gemeindegebiet Emskirchen wird vom 19.10. 18:00 bis zum 21.10 18:00 ein Versammlungsverbot erlassen, um das \n&lt;a href=&quot;20&quot;&gt;Schanzenfest 2.0&lt;/a&gt; zu verhindern.&lt;/p&gt;', '1539900000', 'https://www.kreis-nea.de/uploads/tx_news/emsk_bek_allgemeinverf_01.pdf');
INSERT INTO calendar (title, description, date, sources) VALUES ('Das zweite Schanzenfest / Schanzenherbst.', '&lt;p&gt;Im Vorfeld zur zweiten Auflage des #Schanzenfest gab es wieder einmal ein \n&lt;a href=&quot;../../2018/10/19&quot;&gt;Versammlungsverbot&lt;/a&gt;. &lt;br&gt;\nNach bisherigem Kenntnisstand waren wohl kaum Haider da und es war relativ ruhig. Viele &lt;a href=&quot;https://drachenchronik.gq/tag/polizei&quot; class=&quot;tag&quot;&gt;Streifen&lt;/a&gt;\nwaren unterwegs und haben die wenigen Haider \ndirekt abgefangen und belehrt. Die #Polizei war mit Hundestaffel, USK und BePo am Altschauerberg. Es gab einen unspektakulären Autounfall im Ortskern sowie eine\nkleine Razzia im Roten Herz wegen &quot;Verkauf von illegalen Waren&quot; (Haider haben Mettopoly auf der Dachterrasse gespielt und jemand hat das \nSpiel wohl auch verkauft). Der Gerätewagen wurde angekarrt, um den ASB nachts auszuleuchten.&lt;br&gt;\nDer Dicke wurde also vortrefflichst von seiner Privatarmee beschützt und konnte in Ruhe streamen.&lt;/p&gt;\n\n&lt;p&gt;Bisher erschienene Presseartikel, Versammlungsverbot etc. werden unter \nQuellen verlinkt und aktualisiert, falls etwas Neues kommt!&lt;/p&gt;', '1539986400', 'http://www.spiegel.de/panorama/leute/drachenlord-schanzenfest-beim-youtuber-drachenlord-verhindert-a-1234331.html\nhttps://www.merkur.de/bayern/emskirchen-neustadt-an-aisch-drachenlord-polizei-sperrt-dorf-10345740.html\nhttp://www.nordbayern.de/region/neustadt-aisch/drachenlord-erneut-ausnahmezustand-bei-emskirchen-1.8214986\nhttps://wize.life/themen/fahndung/86333/youtuber-drachenlord-grossaufgebot-der-polizei-schreckt-hass-pilger-ab\nhttp://va.newsrepublic.net/a/6614713188388700677\nhttps://www.bild.de/regional/nuernberg/nuernberg-news/250-gegner-vor-dem-haus-neue-schlacht-gegen-den-drachenlord-57964766.bild.html');
INSERT INTO calendar (title, description, date, sources) VALUES ('Banatic löscht sich.', '&lt;p&gt;#Banatic, eine der Hauptspenderinnen auf #Younow, löscht um 18:52 ihren Account. Seit dem 28.07.2018 hat sie 122.006 Barren mit einem \nEinkaufspreis von $938,31 bis $1.302,85 gespendet.&lt;/p&gt;\n&lt;p&gt;Rainer ging um 19:19 bei Younow online und streamte 16 Minuten Lang. Er saß nur traurig da und heulte dann los.&lt;/p&gt;\n&lt;p&gt;Am darauffolgenden Tag leugnete er, dass der Heulstream etwas mit #Banatic zu tun hatte und er von nichts weiss. Banatic sei für ihn ein \nganz normaler User wie jeder andere auch.&lt;/p&gt;', '1540072800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Die nächtliche Dracheskalation.', '&lt;p&gt;Rainer jagt Haider mit Eiern, Stuhl, Besen, Glasflaschen und einer Eisenstange. \nDabei tritt er laut eigener Aussage in die Scherben der Flaschen und verletzt sich am Fuß.&lt;/p&gt;', '1540591200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Banatic ist zurück.', '&lt;p&gt;Nachdem Banatic sich vor ein paar Tagen &lt;a href=&quot;../../2018/10/21&quot;&gt;gelöscht hat&lt;/a&gt;, ist sie jetzt wieder unter dem Namen &lt;em&gt;Stay_Banatic&lt;/em&gt; zurück und schmeißt gleich mal einige Barren raus.&lt;/p&gt;', '1540767600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Stay_Banatic wird gebannt.', 'Rainer bannt mit Stay_Banatic einen seiner Top Spender.', '1541286000', 'https://twitter.com/DracheSchaut/status/1059031533200269312');
INSERT INTO calendar (title, description, date, sources) VALUES ('Imaginäre Freundin zu Besuch.', 'Rainer macht seinen Jubiläumsstream, in dem eigentlich nichts besonderes passiert. Allerdings ist\nseine imaginäre Freundin mal wieder zu Besuch. Sie bekommt sogar etwas von seinem Krautsalat ab.', '1541804400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Die DrachenChronik geht online.', '&lt;p&gt;Die Drachenlord Chronik geht offiziell unter https://drachenchronik.gq online. Die Domain ist zwar schon seit dem 31.8.2018 registriert,\nhat aber nichts angezeigt. Am 4.11.2019 zog die Seite auf https://drachenchronik.com um.&lt;/p&gt;', '1541890800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Banatic wird (erneut) entbannt.', 'Rainer hat außerhalb seines Streams den User &quot;Stay_Banatic&quot; wieder entbannt.&lt;br&gt;\n[22:31] [YN] User entbannt: Stay_Banatic&lt;br&gt;\nZufällig hat er heute zuvor im Stream einen Song für eine Zuschauerin gespielt, die sich endlich entscheiden soll und ist generell viel an der Tastatur während seiner Streams...&lt;br&gt;\nSiehe auch &lt;a href=&quot;https://drachenchronik.gq/date/2018/10/21&quot;&gt;Banatic löscht sich&lt;/a&gt; und &lt;a href=&quot;https://drachenchronik.gq/date/2018/10/29&quot;&gt;Banatic ist zurück&lt;/a&gt; und &lt;a href=&quot;https://drachenchronik.gq/date/2018/11/4&quot;&gt;Stay_Banatic gebannt&lt;/a&gt;', '1542063600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Bianca zu Besuch in der Schanze.', '&lt;p&gt;Die #YouNow Abonnentin &lt;a href=&quot;https://yntrend.pw/user/44x62g94/activity&quot;&gt;Bianca A.&lt;/a&gt; kündigt im Stream nachmittags an, #Rainer \nin der #Schanze zu besuchen.\nAbends im Stream ist sie dann tatsächlich in der Schanze und streamt mit dem Lord zusammen.&lt;/p&gt;\n&lt;p&gt;Die Userin #Banatic kündigt darauf das Younow Abo und löscht das eigene Profil.&lt;/p&gt;', '1542236400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Bianca löscht sich.', 'Die Userin Bianca A., die &lt;a href=&quot;../../2018/11/15&quot;&gt;am Tag zuvor bei Rainer in der Schanze war,&lt;/a&gt; löscht ihr YouNow Profil und ihren Facebook Account.', '1542322800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Die erste Discord Ära.', '&lt;p&gt;Rainer hat seine Überlegungen wahr gemacht und tatsächlich einen Discord für seine Fans erstellt, nachdem er den \nTeamspeak Server seit einigen Monaten nicht mehr betrieben hat. Am 12.12.2018 wird der Server gesperrt.&lt;/p&gt;\n\n&lt;p class=&quot;alert info&quot;&gt;Wer keine Lust auf Accountswitching hat weil er &quot;ausversehen&quot; gemutet wurde, kann sich \ndas &lt;a href=&quot;https://discordia.me/ptb&quot;&gt;Discord Public Test Build&lt;/a&gt; herunterladen, das kann man parallel laufen lassen \nund sich dort mit alternativen Accounts einloggen.&lt;/p&gt;', '1542754800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Nacktbild geleakt', '&lt;p&gt;Eine Haterin hat sich an den Drachen rangemacht. Natürlich hat es nicht lange gedauert, \nbis er ihr ein Nacktfoto von sich geschickt hat und natürlich ist es auf Twitter gelandet.&lt;/p&gt;\n&lt;p&gt;Außerdem werfen Besucher das Fenster in Rainers Wohn-, Arbeits-, Schlaf-Zimmer ein.&lt;/p&gt;', '1543014000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der gelöschte Blog-Eintrag.', 'Rainer hat einen &lt;a href=&quot;https://archive.fo/391bq#selection-817.0-847.7&quot;&gt;Blogeintrag&lt;/a&gt; über die Verwicklungen mit \n#Banatic gepostet, löscht ihn aber ziemlich bald darauf wieder.&lt;br&gt;', '1543446000', 'https://archive.fo/391bq');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer greift Besucher (und Polizistin?) an.', '&lt;br&gt;&lt;b&gt;Angriff auf Besucher (Polizistin?)&lt;/b&gt;\n&lt;p&gt;Nachdem eine Haidergruppe Rainer mit dem Auto besucht hat, schlägt dieser mit einem Besenstiel auf deren Auto ein. \nNach seinen eigenen Schilderungen in seinem &lt;a href=&quot;https://archive.is/wh2Rq&quot;&gt;Blog,&lt;/a&gt; verletzt er bei einer anschließenden \nRangelei eine Polizistin und eine Haiderin, natürlich nur aus Versehen. Er versucht in seinem Blog die Wogen der Videos, die von diesem \nEreignis früher oder später an die Öffentlichkeit gelangen werden, zu glätten und entschuldigt sich dort schon im \nvoraus für die Vorfälle. Auch bittet er darum, davon abzusehen, ihn weiterhin zu besuchen und kündigt an, sich in psychologische \nTherapie zu begeben.&lt;br&gt;\nEinige Tage später veröffentlicht die Polizei Mittelfranken eine Stellungnahme, in der sie angibt, dass keine Polizistin verletzt wurde.&lt;/p&gt;\n&lt;br&gt;\n&lt;b&gt;Biancer neue Freundin?&lt;/b&gt;\n&lt;p&gt;Im zweiten Stream an diesem Tage bezeichnet Rainer die Userin &lt;a href=&quot;https://yntrend.pw/user/45m23b75/activity&quot;&gt;Mia L.&lt;/a&gt; mehrmals \nals &quot;Schatz&quot; und &quot;Freundin&quot;. Bei der Userin handelt es sich nach ihrer eigenen Aussage um &lt;a href=&quot;../../2018/11/15&quot;&gt;Bianca&lt;/a&gt;, die zuvor \nRainer spontan in der #Schanze besuchte. Auch der selbstgebastelte Adventskalender ist von ihr.&lt;/p&gt;\n&lt;br&gt;\n&lt;b&gt;Discord stillgelegt&lt;/b&gt;&lt;br&gt;\n&lt;p&gt;Der am 21.11.2018 gestartete #Discord ist bereits wieder stillgelegt, alle Räume wurden vorerst geschlossen.&lt;/p&gt;', '1543618800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Mia L. leugnet Bianca zu sein.', 'Heute hat die Younow Userin &lt;a href=&quot;https://yntrend.pw/user/45m23b75/activity&quot;&gt;Mia L.&lt;/a&gt;, die angeblich &lt;a href=&quot;https://drachenchronik.gq/date/2018/12/1&quot;&gt;Rainers neue Flamme&lt;/a&gt; ist, im Chat ihre Aussage, dass sie angeblich &lt;a href=&quot;https://drachenchronik.gq/date/2018/11/15&quot;&gt;Biancer&lt;/a&gt; ist, wieder zurückgezogen (siehe Screenshots im Reiter &quot;Bilder&quot;). Allerdings ist sie mit ziemlicher Sicherheit Biancer, wie sich &lt;a href=&quot;https://drachenchronik.gq/date/2018/12/23/&quot;&gt;später&lt;/a&gt; herausstellen sollte.&lt;br&gt;&lt;br&gt;', '1543791600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('PH Account wird erstellt.', '&lt;p&gt;Der Drachenlord erstellt einen &lt;a href=&quot;../../../go/aHR0cHM6Ly9kZS5wb3JuaHViLmNvbS91c2Vycy9kcmFjaGVvZmZpemllbGxydw==&quot; data-ajax=&quot;false&quot;&gt;Account&lt;/a&gt; bei #Pornhub und lädt dort Nacktbilder von sich hoch. Außerdem gibt es ein Video, in dem er erklärt, warum er den Account angelegt hat.\nDer Account wird am 19.12.2018 wider stillgelegt.&lt;/p&gt;', '1544223600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der Discord Server wird gelöscht.', '&lt;p&gt;Der offizielle &lt;a href=&quot;../../2018/11/21&quot;&gt;Drachenlord Discord Server&lt;/a&gt; wurde gelöscht, vermutlich weil er dort \nseinen #Pornhub Account verlinkt hat.&lt;/p&gt;', '1544569200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Neues PH-Video.', '&lt;p&gt;Rainer veröffentlicht auf seinem &lt;a href=&quot;../../2018/12/8&quot;&gt;Pornhub Kanal&lt;/a&gt; ein Video mit dem Titel &quot;Rassiren und Duschen Mit Happy End&quot;. \nAuf dem Video ist zu sehen, wie er sich unter der Dusche den Intimbereich rasiert und sich danach einen runterholt. Dies ist das erste Video, auf \ndem Rainer nackt zu sehen ist, welches nicht durch Haider veröffentlicht wurde.&lt;/p&gt;\n&lt;strong&gt;Nur für hartgesottene.&lt;/strong&gt;', '1544655600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird die Rundfunklizenz entzogen.', '&lt;p&gt;#Rainer wird von der #BLM die &lt;a href=&quot;../../2018/9/11&quot;&gt;Rundfunklizenz&lt;/a&gt;  entzogen. Er streamt trotzdem weiter.&lt;/p&gt;', '1545087600', 'https://www.die-medienanstalten.de/fileadmin/user_upload/die_medienanstalten/Ueber_uns/Organisation/ZAK_Entscheidungen/Entscheidungsliste_ZAK_-_Zulassungen_2018.pdf');
INSERT INTO calendar (title, description, date, sources) VALUES ('PH stillgelegt / AskFM wiederbelebt.', '&lt;p&gt;Rainer hat alle Inhalte seines am 8.12.2018 erstellten #Pornhub Konto wieder gelöscht.&lt;br&gt;\nDafür hat er seinen alten #AskFM Account wiederbelebt.&lt;/p&gt;', '1545174000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('@lp_drache bei Twitter erstellt.', '&lt;p&gt;Drache erstellt heimlich neuen #Twitter Account. Seinen ersten Tweet mit diesem Account verfasst er aber erst am 4.2.2019, deswegen bleibt er unbemerkt. &lt;br&gt;\nDer Account wurde am 7.2.2019 wieder von Twitter gesperrt.&lt;/p&gt;', '1545346800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer vertreibt Mia / Bianca.', '&lt;p&gt;Am heutigen Tag ist es passiert. Zu einer etwas späten Einsicht kam die #Younow Userin &lt;a href=&quot;https://yntrend.pw/user/45m23b75/activity&quot;&gt;Mia L.&lt;/a&gt;,\nauch bekannt als #Biancer, die am 15.11.2018 bei Rainer in der #Schanze zu Besuch war. \nRainer schmiss sie verbal aus seinem YN-Stream und bannte sie später auch. Offenbar wollte sie nur mit ihm befreundet sein und nachdem \nRainer nicht mehr bekommen hat als das, hat er mal wieder sein wahres Gesicht gezeigt. In späteren Streams redet er dann auf einmal auch davon, \ndass sie zweimal bei ihm war, er aber dann kein Interesse an ihr hätte, da sie zuviel Alkohol konsumiere und ihm ständig Stress machen würde. ...\n&lt;br&gt;&lt;br&gt;\nMia L./Biancer auf Rainers Younow Pinnwand:&lt;/p&gt;\n\n&lt;blockquote&gt;&quot;Rainer, wie Du dich benimmst, ist wirklich unter aller Sau! Aus gekränkter Eitelkeit jetzt so tun, als wäre ich ein Vollidiot, \nindem du mich ignorierst, blockierst und mich dann des Streams \'verweist\', zeigt ja, dass ich mich total in deiner Person getäuscht habe! \nWir haben uns gut verstanden, ich gab nichts auf die Worte der Hater, ich empfand dich wirklich als feinen Kerl mit gutem Herz, \nNUR wollte ich mit Dir keine Beziehung eingehen und bin auch sexuell nicht auf dich eingestiegen! Mich aber deswegen dann wie Dreck zu behandeln, \nzeigt, wie du bist! Ganz armselig was du da machst, und mittlerweile, wenn auch spät, verstehe ich, warum du derart unbeliebt bist! \nBekommt ein kleines Kind nicht, was es will, stampft es auf den Boden und ist beleidigt - Genauso verhälst du Dich! Das 24. Päckchen und \ndie scharfen Soßen darin, kannst du so wie es ist, zu deinem anderen Schrott in den Hof werfen, denn verdient hast du so etwas herzlich \ngemachtes nicht! Das war\'s! Vergeudete Zeit ...&quot; &lt;br&gt;&lt;br&gt;\n[...] &quot;Und ein Tipp noch für weitere weibliche Kontakte: Dein Drängen ist absolut uncharmant, belästigend und abstoßend!&quot;&lt;/blockquote&gt;', '1545519600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der Gerichtstermin wird abgesagt.', 'Der anberaumte Gerichtstermin, der für den 24.1.2019 angesetzt war, wurde abgesagt. Rainer wird einen schriftlichen Strafbefehl für die KV erhalten, die Hauptverhandlung findet nicht statt.', '1546556400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Wingl wittert Entjungferung - Lena-Leaks.', 'Ein Beerchen namens &quot;Lena Krüger&quot; entlockte Rainer einige Nacktbilder (u.a. das Ganzkörperbild im Garten vom Sommer \'18) und veröffentlichte nach und nach Gespräche zwischen sich und Rainer auf Youtube unter dem Titel &lt;a href=&quot;https://www.youtube.com/watch?v=duHUo2Y7TSg&amp;t=432s&quot;&gt;&quot;Wingl wittert Entjungferung&quot; (WwE)&lt;/a&gt;.&lt;br&gt;\nBeerchen auf Twitter: &lt;a href=&quot;https://twitter.com/paperplane1510&quot;&gt;@paperplane1510&lt;/a&gt;', '1546729200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Keine Glasfaser / Das Banatic Interview.', '&lt;p&gt;Rainer beschwert sich in seinem Stream über die Telekom, da sie ihm wegen schlechter Bonität keine Glasfaser legen will \n(oder weil Rainerle nicht einsieht, die ca. 800 Euro zu zahlen, die nötig wären, um den Anschluss zu legen. Oder beides). \nTelekommitarbeiter verabschiedet sich laut ihm mit &quot;Grüße in die #Schanze!&quot; am Telefon. \nAußerdem gibt #Banatic ein Interview über die Zeit beim Drachenlord.&lt;/p&gt;\nSiehe auch &lt;a href=&quot;https://drachenchronik.gq/date/2018/10/21&quot;&gt;Banatic löscht sich&lt;/a&gt; und &lt;a href=&quot;https://drachenchronik.gq/date/2018/10/29&quot;&gt;Banatic ist zurück&lt;/a&gt; und &lt;a href=&quot;https://drachenchronik.gq/date/2018/11/4&quot;&gt;Stay_Banatic gebannt&lt;/a&gt;', '1546902000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('&quot;Auf Wegen des Lebens&quot; wird geleakt.', 'Rainers neues schriftstellerisches Werk wurde von #Banatic geleakt. \nDer AsA veröffentlicht den Leak. Dort gibt es auch eine &lt;a href=&quot;https://altschauerberganzeiger.com/post/181891955131/090119-eine-geschichte-ist-nicht-die&quot;&gt;Zusammenfassung.&lt;/a&gt;&lt;br&gt;\nPDF &quot;Auf Wegen des Lebens&quot;: &lt;a href=&quot;https://www.docdroid.net/XCBQfH1/auf-wegen-des-lebens-final-ohne-cover.pdf&quot;&gt;Link&lt;/a&gt;', '1547074800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('PH Account wird reaktiviert.', 'Nachdem Rainer am 8.12.2018 einen #Pornhub Account eröffent hat, die Inhalte am 19.12.2018 wieder gelöscht hat, lädt er sie jetzt wieder hoch. Seiner Aussage nach, weil ihn wohl einige Leute darum gebeten haben. Es ist auch wieder eine Liste seiner geschauten Pornos geleakt.&lt;br&gt;&lt;br&gt;\nRainers Statemant auf PH:\n&lt;blockquote&gt;Hallo Ich habe mich nach längeren anfragen und bitten dazu entschieden die Videos wieder hochzuladen. \nOb neues Kommt weis ich nicht ich denke ab und ab Bilder vor allem auch von Spielsachen wird es sicher geben alles weitere weis ich noch nicht. \nHoffe ihr freut euch  Und danke für die Freundlichen Poste Hätte nicht gedacht das es hier so coole menschen gibt  Schönen Sonntag euch noch&lt;/blockquote&gt;', '1547334000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer, der PH-Pornostar.', 'Rainer hat auf seinem Pornhub Account jetzt den Status eines Pornomodels, d.h. er kann dort nun auch Spenden nehmen und verdient Geld durch Klicks.', '1547679600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Gast bei Calidri.', '&lt;p&gt;Rainer ist beim YouNower Calidri zu Gast im Stream, um ihn dabei zu unterstützen, eine Busreise zu gewinnen, die an Zuschauer verlost werden soll. Außerdem werden Rainer Silvesterböller neigschmaßt. &lt;br&gt;\nNach einigen wenigen Streams lernt Calidri den Kotmidas kennen und bekommt unter anderem Pizza nachhause bestellt. Am 20.1.2019 blockt Calidri dann schließlich den Drachenlord auf YouNow. Dieser merkt dies allerdings erst am &lt;a href=&quot;https://drachenchronik.gq/date/2019/1/24&quot;&gt;24.01.2019&lt;/a&gt; und mettet in einem seiner Younow Streams dann gegen Calidri ab&lt;/p&gt;', '1547938800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Die Boxkampf Ankündigung.', 'Zum einen bemerkt Rainer heute, dass er vom &lt;a href=&quot;https://www.younow.com/_Calidri_&quot;&gt; YouNower Calidri&lt;/a&gt; geblockt wurde und liefert einen Ragestream darüber ab.&lt;br&gt; &lt;br&gt;\nZum anderen ist er später beim &lt;a href=&quot;https://www.younow.com/LachFlat&quot;&gt; YouNower Lachflat&lt;/a&gt; zu Gast, \nder ihm vorschlägt, sie sollten einen Boxkampf zwischen Rainer und dem &lt;a href=&quot;https://www.youtube.com/channel/UCAVDr4sYDAjBXoWaWhXJ3OQ&quot;&gt;Youtuber&lt;/a&gt; / &lt;a href=&quot;https://www.younow.com/hansentertainmentOffziell&quot;&gt;YouNower&lt;/a&gt;  HansEntertainment organisieren, der in Köln stattfinden soll. \nRainer erst skeptisch, lässt sich dann aber so sehr von Hans\' Geschreibe im Chat triggern, dass er schließlich zustimmt.&lt;br&gt;\nRainer beschließt (wiedermal) voller Elan, sportlich zu werden. Ab jetzt kommen nur noch drei Würfelzucker in den Milchkaffee. \nEr möchte wieder Trainings-Videos und jeden Tag Sport machen, gesündere Ernährung, etc.&lt;br&gt;&lt;br&gt;\nAm &lt;a href=&quot;https://drachenchronik.gq/date/2019/1/31&quot;&gt;31. Januar&lt;/a&gt; bläst Lachflat die Sache wieder ab.', '1548284400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('YouTube Kanal wird für Uploads gesperrt.', '&lt;p&gt;Rainer hat auf seinem #Youtube Kanal seine alten Videos erneut hoch geladen. Der Youtube Algorithmus erkannte die Videos als\nKopien der Videos auf verschiedenen Reupload Kanälen. So wurde Rainers Hauptkanal vorerst für erneute Uploads gesperrt.&lt;/p&gt;\n&lt;p&gt;Rainer erklärt im #YouNow Stream, dass erst mal keine neuen Videos auf seinem Hauptkanal kommen werden.&lt;/p&gt;', '1548543600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Erstmal keine Streams.', '&lt;p&gt;Um 14:35 schreibt Rainer auf seiner YouNow Pinnwand: &lt;blockquote&gt;Leute ich Kann im moment eine längere zeit nicht streamen&lt;/blockquote&gt;\nDanach bannt er erst mal einige User bei Younow.&lt;br&gt;&lt;br&gt;\nUm 1:44 Nachts heizt er die Gerüchteküche dann nochmal an, anstatt einfach ein Statement abzugeben:&lt;br&gt;\n&lt;blockquote&gt;Warum ich nicht streame hat keinen so spannenden grund wie ihr zu glauben scheint also lasst es mal so unsinnige gerüchte zu streuen&lt;/blockquote&gt;&lt;/p&gt;', '1548630000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('BLM untersagt Rainer das streamen.', '&lt;p&gt;Die Bayerische Landeszentrale für neue Medien (#BLM) &lt;a href=&quot;https://twitter.com/BLM_Bayern/status/1090233038850662400&quot;&gt;untersagt&lt;/a&gt; \nRainer, live zu streamen, bis er eine neue Lizenz beantragt hat und diese dann geprüft und genehmigt wurde.&lt;/p&gt;', '1548716400', 'https://www.computerbild.de/artikel/cb-News-Internet-YouTuber-Drachenlord-verboten-22931041.html');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der Boxkampf wird abgesagt.', 'Der Younow Streamer Lachflat sagt in seinem Stream den Boxkampf zwischen Rainer und Hans Entertainment ab. Drache schaut den Stream und schreibt fleißig verwirrende Chatnachrichten.', '1548889200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Der BLM-Meltdown / Der letzte YouNow-Stream.', '&lt;p&gt;Der Drache war heute entgegen seinem Streamverbot auf Younow unterwegs und hat einen kurzen Stream abgeliefert, in dem er sehr besiegt \nist und stark aus den Augen schwitzt.&lt;/p&gt;\n&lt;blockquote&gt;[13:58] [YN-Pinnwand] Diesesmal nicht ich überlasse euch den sieg herzlichen glückwunsch ider habt erfolgreichj das leben eines anderen zerstört hoffe ihr seit stolz auf euch&lt;/blockquote&gt;\n&lt;blockquote&gt;[14:03] [YN-Pinnwand] [1/2] Herzlichen Glückwunsch an die hater euer plan mit einer Faik nachricht bei der BLM das ich angeblich eine rundfunklizens beantragt habe haben sie geglaubt und ihr habt wiedermal ohne einen finger krum zu machen jemanden besiegt der seit jahren für&lt;/blockquote&gt;\n&lt;blockquote&gt;[14:03] [YN-Pinnwand] [2/2] seinen traum kämpft hoffe euer unverdinter sieg hat sich gelohnt wen wollt ihr als nächstes fertig machen jetzt wo ich weg bin?&lt;/blockquote&gt;&lt;br&gt;&lt;br&gt;\nDie BLM &lt;a href=&quot;https://twitter.com/BLM_Bayern/status/1091315635878088705&quot;&gt;antwortet&lt;/a&gt; sogleich auf Rainers kurzen Stream: &lt;br&gt;\n&lt;blockquote&gt;In Sachen Drachenlord 1. Livestream ist aus formalen Gründen untersagt worden. Antrag auf Rundfunkzulassung enthielt nicht alle erforderlichen Unterlagen. Sie wurden trotz Nachfrage nicht nachgereicht. Eine weitergehende inhaltliche Prüfung hat deshalb nicht stattgefunden.&lt;/blockquote&gt;\n\n&lt;blockquote&gt;In Sachen Drachenlord 2. Der Livestream-Betreiber behauptet nun, den Antrag nicht selbst gestellt zu haben. Das ändert nichts an der Untersagung, da Livestream-Angebote als Rundfunk eingestuft werden. Weitere Infos: www.blm.de&lt;/blockquote&gt;&lt;br&gt;&lt;br&gt;\n\nArtikel zum Thema: &lt;a href=&quot;http://www.nordbayern.de/region/landesmedienanstalt-legt-drachenlord-livestream-still-1.8561024&quot;&gt;Nordbayern,&lt;/a&gt; \n&lt;a href=&quot;https://www.bild.de/regional/nuernberg/nuernberg-news/weil-er-keinen-antrag-stellte-sendeverbot-fuer-den-drachenlord-59900370.bild.html&quot;&gt;Bild,&lt;/a&gt; \n&lt;a href=&quot;http://www.gamesaktuell.de/Panorama-Thema-233992/News/Drachenlord-Lizenz-Livestream-Behoerde-bayrische-Landeszentrale-neue-Medien-1274587/&quot;&gt;Gamesaktuell,&lt;/a&gt;\n&lt;a href=&quot;https://www.merkur.de/bayern/emskirchen-drachenlord-behoerde-stoppt-live-streams-youtuber-reagiert-sofort-10345740.html&quot;&gt;Merkur&lt;/a&gt;', '1548975600', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Bild Artikel zu PH.', 'Auf Bild erscheint ein Artikel zu Rainers PH Aktivität. &lt;a href=&quot;https://www.bild.de/regional/nuernberg/nuernberg-news/rainer-w-macht-jetzt-pornos-der-drache-zeigt-seinen-kleinen-lord-59947382.bild.html&quot;&gt;Zum Artikel&lt;/a&gt;&lt;br&gt;\nAußerdem twittert Drache heute zum ersten mal auf seinem am 21.12.2018 unbemerkt erstellten neuen &lt;a href=&quot;https://twitter.com/lp_drache&quot;&gt; Twitter Account&lt;/a&gt;.&lt;br&gt;\nDer Account wurde am 7.2.2019 wieder von Twitter gesperrt.', '1549234800', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('@lp_drache bei Twitter gesperrt.', 'Nachdem schon @DracheOffiziell und @RainerWinklerDO gesperrt wurden, wurde jetzt auch @lp_drache gesperrt.', '1549494000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer setzt sich über Streamverbot hinweg.', '&lt;p&gt;Rainer setzt sich über das behördlich verhängte Streamverbot hinweg, da er der Meinung ist, dass dieses nur für &quot;DrachenLord&quot; gilt und nicht für ihn.&lt;/p&gt;', '1549580400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Neue Allgemeinverfügung.', 'Der Markt Emskirchen erlässt erneut eine &lt;a href=&quot;https://drachenchronik.gq/fileserver/file/Y3dyMG02YWU1eC9BbGxnVmVyZl9BU0JfMjAxOTAyMjcucGRm&quot;&gt; Allgemeinverfügung&lt;/a&gt; über den Altschauerberg, nach der es verboten ist, sich zu versammeln, zu vermummen, Feuerwerkskörper zu zünden und ähnliches. Die Verfügung gilt bis zum 30.06.2019.', '1551222000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer schlägt seine eigenen Scheiben ein.', '&lt;p&gt;Rainer schlägt während eines Haider-Besuchs zwei seiner Fenster gleich selber ein.&lt;/p&gt;', '1551481200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird zum Terroristen.', 'Kurz nach dem Terroranschlag in Utrecht werden über Twitter Bilder und Videoausschnitte vom Drachenlord mit dem Hashtag Utrecht verbreitet. Ein türkischer TV-Sender greift dies auf und strahlt Bilder von Rainer aus, die ihn als mutmaßlichen Täter bezeichnen.\n\n&lt;a href=&quot;https://www.heute.at/digital/multimedia/story/Tuerkischer-Sender-24-TV-macht-Drachenlord-Rainer-Winkler-zum-Killer-von-Utrecht-48953637&quot;&gt;Artikel dazu von heute.at&lt;/a&gt;&lt;br&gt;', '1552863600', '');
