INSERT INTO calendar (title, description, date, sources) VALUES ('Haider hackt mit Pornoschnipseln auf YouNow Streams.', '&lt;p&gt;Ein &lt;a href=&quot;https://twitter.com/z3r0dy&quot;&gt; Haider&lt;/a&gt; findet eine #YouNow Schwachstelle, die es ihm ermöglicht, aktive Streams zu kapern. \nZwei Tage zuvor wurde auf diese Weise das Gummipuppen-Video in Rainers Stream reingerendert.&lt;br&gt;\nNun werden diese Pornoschnippsel auch bei anderen YouNow\'ern eingespielt und diese denken, der Drache hätte sie gehackt.&lt;/p&gt;\n\n&lt;a href=&quot;https://drachiv.eu/media/video/compilations/Rainer-hackt-Striems.mp4&quot;&gt;Ein Link zum Video beim Drachiv&lt;/a&gt;', '1557612000', '');
