INSERT INTO calendar (title, description, date, sources) VALUES ('ARD bringt Folge von &quot;Hubert ohne Staller&quot; basierend auf Rainer raus / DMs gelöscht.', '&lt;h3&gt;Rainer löscht alle neueren Dragon Mondays&lt;/h3&gt;\n&lt;p&gt;Rainer löscht sämtliche Videos seines Formats #DragonMonday von seinem Kanal, nachdem er schon einen Strike erhalten hatte, da er ein Intro \nfür die DMs verwendet, welches ihm damals ein Fan erstellt hat. Rainer hat aber weder gefragt, ob er das Intro verwenden kann, noch gab es irgendeine Gegenleistung.\nEs wird vermutet, dass Rainer den weiteren Strikes durch die Löschung der DMs entgehen will.&lt;/p&gt;&lt;br&gt;\n\n&lt;h3&gt;ARD bringt eine Folge &quot;Hubert ohne Staller&quot; raus&lt;/h3&gt;\n...die offensichtlich auf dem Drachenlord basiert. &lt;a href=&quot;https://www.daserste.de/unterhaltung/serie/hubert-ohne-staller/videos/tod-dem-koenig-folge-148-video-102.html&quot;&gt;Zur Folge in der Mediathek&lt;/a&gt;&lt;br&gt;', '1611702000', 'https://twitter.com/MaxHecks/status/1354478587622125569');
