INSERT INTO calendar (title, description, date, sources) VALUES ('Löschung fast aller Videos / Videos nur noch für Kanal-Mitglieder.', '&lt;p&gt;Wegen Verwendung eines weiteren Intros, für das Rainer keine Rechte\nbesitzt, bestätigte #Youtube einen zweiten Copyright Strike. In der Folge löschte\nRainer mitten in der Nacht panisch all seine Videos. Die\n&lt;a href=&quot;../../../music/search?t=2&quot;&gt;Musikvideos&lt;/a&gt; ließ er stehen. Auch das\n&lt;a href=&quot;../../2018/05/27&quot;&gt;Effekte-Video&lt;/a&gt;, die\n&lt;a href=&quot;../../../tag/kdi&quot; class=&quot;tag&quot;&gt;&quot;Kurzfilme der Idioten&quot;&lt;/a&gt; und die\nAnime-Videos wurden von der Löschung verschont, sind nun aber nur noch für\nKanalmitglieder aufrufbar.&lt;/p&gt;\n&lt;p&gt;Den Kanal stellte er auf &quot;nur für Kanalmitglieder&quot;, so dass alle\nzukünftigen Videos und Streams nur noch für zahlende Mitglieder verfügbar sind.\n&lt;/p&gt;', '1612220400', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer wird im Radio erwähnt.', '&lt;p&gt;Rainer wird von dem Radiosender &quot;Antenne Bayern&quot; kurz erwähnt. Grund: Geänderte\nStraßennamen auf Google Maps wie zB. der &quot;Freundin aus Denken-Weg&quot; oder auch die\n&quot;Lügengasse&quot;.&lt;/p&gt;', '1612393200', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainers Nachbarn rufen die Polizei.', '&lt;br&gt;Rainer dreht in seinem Discord Stream die Musik auf, bis seine Nachbarn die Polizei wegen Ruhestörung rufen. &lt;br&gt;', '1612566000', '');
INSERT INTO calendar (title, description, date, sources) VALUES ('Rainer erhält einen dritten YT-Strike.', '&lt;p&gt;Rainer kriegt einen dritten Strike und kann vorerst keine Videos mehr\nhochladen. Der Grund dafür ist vermutlich die Verwendung eines Bildes im\nThumbnail seines Videos vom 02.02.2021, für das er keine Rechte hat.&lt;/p&gt;', '1612738800', '');
